                    //(현재ポストの数と比較して、最後のポストの場合には
                    //異なるデザインを適用するため)
                    $lastPost = 3;
                    // custom post type 'works'を取得
                    $args = array(
                        'post_type' => 'works',
                        'posts_per_page' => $lastPost,
                    );
                    $works = new WP_Query($args);
                    $postCount = 1;
                    if($works->have_posts()):
                        while($works->have_posts()):
                            $works->the_post();
                            
                 ?>
            <div class="p-work-post">
                <a href="<?php the_permalink(); ?>" class="p-work-post__content">
                    <div class="p-work-post__head">
                        <span class="p-work-post__num"><?php echo "0" .$postCount?></span>
                        <div class="p-work-post__img">
                            <img src="<?php the_field('thumbnail');?>" alt="<?php the_field("thumbnail-alt") ?>">
                        </div>
                    </div>
                    <div class="p-work-post__body">
                        <h3 class="p-work-post__title">
                            <?php the_title();?>
                        </h3>
                        <p class="p-work-post__part">
                            WEB | <?php the_field('part');?>
                        </p>
                    </div>
                    <div class="p-work-post__footer">
                        <div class="p-work-post__tools">
                            <?php $tags = get_field("tool");
