                                    $tagsArray = explode(', ',$tags);
                                    foreach($tagsArray as $tag):
                             ?>
                            <span class="p-work-post__tool-tag c-tool-tag">
                                <?php echo $tag;?>
                            </span>
                            <?php endforeach; ?>
                            <?php endif; ?>
                        </div>
                    </div>
                    <!-- 最後の投稿の場合、worksページに移動するボタンを作成 -->
                    <?php if($postCount == $lastPost): ?>
                    <?php endif;?>
                </a>
            </div>
            <?php  
                    $postCount++;
